import logo from './logo.svg';
import './App.css';
import { Routes, Route } from "react-router-dom"
import {Home, ViewTeams, AddTeam, PageNotFound } from "./pages"
import { useState, useEffect } from 'react';

function App() {
  const [teams, setTeams] = useState([]);
  useEffect(() => {
    fetch('/api/teams')
      .then((response) => response.json())
      .then(setTeams)
  }, []);

  const [playerToAdd, setPlayerToAdd] = useState([]);
  useEffect(() => {
    const add = async () => {

      const result = await  fetch('/api/addPlayer',{
          method: "POST",
          body: JSON.stringify({playerName: playerToAdd}),
          headers:{"Content-Type": "application/json",}
      });
      const body = await result.json();
      console.log(body);
      setTeams(body.playerToAdd)
  }
  add();
  console.log(`Players ${playerToAdd}`)  

  }, []);

  const [playersAdded, setPlayersAdded] = useState([]);
  useEffect(() => {
    fetch('/api/playersAdded')
      .then((response) => response.json())
      .then(setPlayersAdded)
  }, []);






  return(
    <div>
      <Routes>
        <Route path="/" element={<Home teams={teams} setTeams={setTeams} />}/>
        <Route path="/viewTeams" element={<ViewTeams teams={teams} setTeams={setTeams} />}/>
        <Route path="/addTeam" element={<AddTeam teams={teams} setTeams={setTeams} playerToAdd={playerToAdd} setPlayerToAdd={setPlayerToAdd} 
        playersAdded={playersAdded} setPlayersAdded={setPlayersAdded}/>}/>
        <Route path="*" element={<PageNotFound />}/>
      </Routes>
    </div>
  )
}
export default App;
